<?php
// حفظ صورة اللوغو من الذاكرة المؤقتة إلى ملف

// تحديد مسار حفظ الصورة
$logoPath = __DIR__ . '/public/images/logo/volunteer-logo.png';

// بيانات الصورة المدمجة في base64 (الصورة الخضراء التي تمثل اللوغو)
 

// تحديد مسار حفظ الصورة
$logoPath = __DIR__ . '/public/images/logo/volunteer-logo.png';

// بيانات الصورة المدمجة في base64 (الصورة الخضراء التي تمثل اللوغو)
$base64Image = '';

// تحويل البيانات المدمجة إلى بيانات ثنائية
$imgData = base64_decode(preg_replace('#^data:image/\w+;base64,#i', '', $base64Image));

// إنشاء المجلد إذا لم يكن موجوداً
$dir = dirname($logoPath);
if (!is_dir($dir)) {
    mkdir($dir, 0755, true);
}

// حفظ الصورة
if (file_put_contents($logoPath, $imgData)) {
    echo "تم حفظ الصورة بنجاح في: " . $logoPath;
} else {
    echo "حدث خطأ أثناء حفظ الصورة";
}